mapper.select:

select id, title, profile
from blog
where id =#{id }

mapper.update:

update blog 
set title = #{title },
	profile = #{profile }
where id = #{id }

mapper.select:

select if(count(*)>1,true,false)
from category
where id = #{id }


select no, title, contents, reg_date as regDate, category_no as categoryNo
from post
where no = #{no }
